import React, {useEffect} from 'react'
import { useParams } from "react-router-dom";

const Post = () => {

    let { id } = useParams();

    useEffect(() => {
        axios.get(`http://localhost:3001/posts/byId/${id}`).then((response) => {
          setPostObject(response.data);
        });
      });

  return (

    <div>
      {id}
    </div>
  )
}

export default Post
